<?phpnamespace App\Http\Controllers;use Illuminate\Http\Request;use DB;class CorrelativosController extends Controller{    public function __construct()    {    }    public function CreateUpdateCorrelativos(Request $request, $Accion){    $IdEmpresa=$request->get('IdEmpresa');    $IdSucursal=$request->get('IdSucursal');    $IdTipoDocumento=$request->get('IdTipoDocumento');    $Serie=$request->get('Serie');    $DocNro=$request->get('DocNro');    $Defecto=$request->get('Defecto');    $ImprimirEn=$request->get('ImprimirEn');    $Correlativos = DB::select('call Lo_Man_zg_correlativos(?,?,?,?,?,?,?,?)',    array(     $Accion,     $IdEmpresa,     $IdSucursal,     $IdTipoDocumento,     $Serie,     $DocNro,     $Defecto,     $ImprimirEn    ));    return response()->json($Correlativos, 201);    }    public function GetCorrelativos($idEmpresa) {    $Accion = 'S01';     $Correlativos = DB::select('call Lo_Man_zg_correlativos(?,?,?,?,?,?,?,?)',    array(     $Accion,     $idEmpresa,     null,     null,     null,     null,     null,     null    ));    return response()->json($Correlativos, 200);    }    public function DeleteCorrelativos($IdEmpresa,$IdSucursal,$IdTipoDocumento,$Serie){    $Accion = 'M03';     $Correlativos = DB::select('call Lo_Man_zg_correlativos(?,?,?,?,?,?,?,?)',    array(     $Accion,     $IdEmpresa,$IdSucursal,$IdTipoDocumento,$Serie,     null,     null,     null    ));    return response()->json($Correlativos, 200);    }    public function UpdateEstadoCorrelativos($IdEmpresa,$IdSucursal,$IdTipoDocumento,$Serie,$Activo){    $Accion = 'M04';     $Correlativos = DB::select('call Lo_Man_zg_correlativos(?,?,?,?,?,?,?,?)',    array(     $Accion,     $IdEmpresa,$IdSucursal,$IdTipoDocumento,$Serie,     null,     null,     null    ));    return response()->json($Correlativos, 200);    }}